import React, { useState, useEffect } from 'react'; 
import axios from 'axios';

function AdminPage() {
  const [user, setUser] = useState(null); // Changed from an array to an object

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      // Redirect to login page if not authenticated
      window.location.href = '/';
    }
    console.log("JWT Token:", token); // Log the token

    axios.get('http://localhost:8084/user/info', {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
    .then(response => {
      console.log(response.data); // Log the user object
      setUser(response.data); // Set user as an object
    })
    .catch(error => console.error('Error fetching user:', error));
  }, []);
  
  // Render user info instead of mapping through an array
  return (
    <div>
      <h2>Admin Dashboard</h2>
      {user ? ( // Check if user is not null
        <ul>
          <li key={user.id}>
            {user.username} ({user.role})
            {/* The delete button logic here is not applicable for a single user */}
          </li>
        </ul>
      ) : (
        <p>Loading user information...</p> // Display loading state
      )}
    </div>
  );
}

export default AdminPage;
